package com.joe;

public class Triangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	for(int j = 1;j<=5;j++)//執行次數
	{for(int k = 4;k>=j;k--)//遞增"<=",地減">="
		System.out.print(" ");
	for(int i = 1;i<=2*j-1;i++)
		System.out.print("*");
		System.out.println();
	}
		
		
		
		
			
		}
			
			
			
		}
		
	

